  --popover: 220 12% 14%;

  --popover-foreground: 220 8% 95%;

  --popover-border: 220 12% 22%;

  --primary: 210 85% 45%;

  --primary-foreground: 210 85% 98%;

  --secondary: 220 10% 20%;

  --secondary-foreground: 220 8% 85%;

  --muted: 220 10% 16%;

  --muted-foreground: 220 8% 65%;

